import { Chip, Divider, Tab, Tabs } from "@mui/material";
import { Box } from "@mui/system";
import axios from "axios";
import React, { useEffect, useState } from "react";

export default function ProblemStatement(props) {
  const { problem_id, lab_id } = useParams();

  const [state, setState] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  const [tabState, setTabState] = useState(0);

  useEffect(async () => {
    const res = axios({
      method: "POST",
      url: "http://localhost:1001/api/lab",
      data: {
        type: "problem/get",
        problem_id: problem_id,
        lab_id: lab_id,
      },
    });
    if (res.data) {
      setIsLoading(false);
      setState(res.data);
    } else {
      console.log("Error fetching problem!");
    }
  });

  if (isLoading) return <div>{/* Skeleton */}</div>;
  return (
    <Tabs>
      <Tab label="Problem" />
    </Tabs>
  );
