        <Tabs onChange={() => setTabState(1 - tabState)}>
          <Tab label="Problem" />
          <Tab label="Solution" />
        </Tabs>
      </Box>

      <div className="problem-statement">state.problem_statement</div>
      <Divider />
      <div className="problem-tests">
