import axios from "axios";
import { Box } from "@mui/system";
import { LinearProgress } from "@mui/material";
import { Navigate } from "react-router";
import React, { useEffect, useState, useContext } from "react";

export const AuthContext = React.createContext();

// The Auth Provider will provide the online status of
// the user whenever we try to access a private route
export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [lastVerificationTimestamp, updateVerificationTimestamp] = useState(
    new Date(0)
  );

  // if the verification with the server happened just 10 minutes ago
  // then show the view otherwise verify the acess token with the server
  useEffect(() => {
    if (
      currentUser != null &&
      Date.now() - lastVerificationTimestamp < 10 * 60 * 60 * 1000
    ) {
      return;
    }

    axios({
      method: "post",
      url: "http://localhost:8001/auth/verify",
      timeout: 2000,
      retries: 3,
    })
      .then((res) => {
        setIsLoading(false);
        updateVerificationTimestamp(Date.now().getTime());
        setCurrentUser(res.data.username);
      })
      .catch((err) => {
        console.log(err);
      });
  }, [currentUser]);

  return (
    <AuthContext.Provider
      value={{ currentUser, isLoading, setCurrentUser, setIsLoading }}
    >
      {children}
    </AuthContext.Provider>
  );
};

// PrivateRoute encloses all protected pages which need authentication
export const PrivateRoute = ({ children }) => {
  const { currentUser, isLoading } = useContext(AuthContext);

  if (isLoading) {
    return (
      <Box sx={{ width: "100%", marginTop: "0" }}>
        <LinearProgress />
      </Box>
    );
  }

  return currentUser ? children : <Navigate to="/login" replace="true" />;
};
