import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";

const AuthContext = React.createContext();

export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();

  // we'll set the username value if it's there in the localStorage
  const localUsername = localStorage.getItem("username");
  if (localUsername) {
    setCurrentUser(localUsername);
  }

  const [isLoading, setIsLoading] = useState(true);

  // we'll still verify if the use is still logged in at the
  // backend or not
  useEffect(() => {
    updateOnline(localUsername, (err, _) => {
      if (err) {
        console.log(`UPDATE_ONLINE_ERR: ${err}`);
        localStorage.removeItem("username");
        navigate("/login", { replace: true });
      } else {
        setCurrentUser(localUsername);
        setIsLoading(false);
      }
    });
  }, []);

  return (
    <AuthContext.Provider value={{ currentUser, isLoading }}>
      {children}
    </AuthContext.Provider>
  );
};
