import "./App.css";

import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";

// import Editor from "./components/code_editor/editor";
import NavBar from "./features/navbar/navbar";
import NotFound from "./features/404/404";
import SignIn from "./features/auth/signin";
import SignUp from "./features/auth/signup";
import { AuthProvider } from "./authContext";

function requireAuth(nextState, replace, next) {
  const loggedIn = JSON.parse(localStorage.getItem("username"));
  if (!loggedIn) {
    replace({
      pathname: "/login",
      state: { nextPathname: nextState.location.pathname },
    });
  }
  next();
}

const LoginContainer = () => {
  return (
    <div>
      <Routes>
        <Route
          exact
          path="/"
          render={(isLoggedIn) =>
            isLoggedIn ? <Navigate to="/login" /> : <Navigate to="/home" />
          }
        />
        <Route path="/login" component={SignIn} />
        <Route path="/register" component={SignUp} />
      </Routes>
    </div>
  );
};

const DefaultContainer = () => {
  return (
    <div>
      <NavBar />
      <Routes></Routes>
    </div>
  );
};

export default function App() {
  return (
    <AuthProvider>
      <Route path="/labs" element={<Labs />} />
      <Route exact path="/lab/:lab_id" element={<LabDefault />} />
      <Route path="/lab/:lab_id/problem/:problem_id" element={<Editor />} />
      <Route element={<NotFound />} />
    </AuthProvider>
  );
}
